CREATE FORCE VIEW "PUBLIC"."USER_KEYWORDS_VIEW"("USER_ID", "USER_NAME", "KEYWORDS") AS
SELECT
    "SU"."ID" AS "USER_ID",
    "SU"."NAME" AS "USER_NAME",
    LISTAGG("K"."NAME", ', ') WITHIN GROUP (ORDER BY NULL) AS "KEYWORDS"
FROM "PUBLIC"."SITE_USER" "SU"
LEFT OUTER JOIN "PUBLIC"."USER_KEYWORD" "UK"
    ON "SU"."ID" = "UK"."USER_ID"
LEFT OUTER JOIN "PUBLIC"."KEYWORD" "K"
    ON "UK"."KEYWORD_ID" = "K"."ID"
GROUP BY "SU"."ID", "SU"."NAME";        
CREATE FORCE VIEW "PUBLIC"."CLUB_KEYWORDS_VIEW"("CLUB_ID", "CLUB_TITLE", "KEYWORDS") AS
SELECT
    "C"."ID" AS "CLUB_ID",
    "C"."TITLE" AS "CLUB_TITLE",
    LISTAGG("K"."NAME", ', ') WITHIN GROUP (ORDER BY NULL) AS "KEYWORDS"
FROM "PUBLIC"."CLUB" "C"
LEFT OUTER JOIN "PUBLIC"."CLUB_KEYWORD" "CK"
    ON "C"."ID" = "CK"."CLUB_ID"
LEFT OUTER JOIN "PUBLIC"."KEYWORD" "K"
    ON "CK"."KEYWORD_ID" = "K"."ID"
GROUP BY "C"."ID", "C"."TITLE";               
